dad0205d-129f-4c68-b288-e32bdbf885dd.policy:
{
  "Version" : "2012-10-17",
  "Id" : "key-cloudtrail",
  "Statement" : [ {
    "Sid" : "Enable IAM User Permissions",
    "Effect" : "Allow",
    "Principal" : {
      "AWS" : "arn:aws:iam::389435993474:root"
    },
    "Action" : "kms:*",
    "Resource" : "*"
  }, {
    "Sid" : "Allow CloudTrail to encrypt logs",
    "Effect" : "Allow",
    "Principal" : {
      "Service" : "cloudtrail.amazonaws.com"
    },
    "Action" : "kms:GenerateDataKey*",
    "Resource" : "*",
    "Condition" : {
      "StringLike" : {
        "kms:EncryptionContext:aws:cloudtrail:arn" : "arn:aws:cloudtrail:*:389435993474:trail/*"
      }
    }
  }, {
    "Sid" : "Allow CloudTrail to describe key",
    "Effect" : "Allow",
    "Principal" : {
      "Service" : "cloudtrail.amazonaws.com"
    },
    "Action" : "kms:DescribeKey",
    "Resource" : "*"
  }, {
    "Sid" : "Allow principals in the account to decrypt log files",
    "Effect" : "Allow",
    "Principal" : {
      "AWS" : "*"
    },
    "Action" : [ "kms:Decrypt", "kms:ReEncryptFrom" ],
    "Resource" : "*",
    "Condition" : {
      "StringEquals" : {
        "kms:CallerAccount" : "389435993474"
      },
      "StringLike" : {
        "kms:EncryptionContext:aws:cloudtrail:arn" : "arn:aws:cloudtrail:*:389435993474:trail/*"
      }
    }
  }, {
    "Sid" : "Allow alias creation during setup",
    "Effect" : "Allow",
    "Principal" : {
      "AWS" : "*"
    },
    "Action" : "kms:CreateAlias",
    "Resource" : "*",
    "Condition" : {
      "StringEquals" : {
        "kms:CallerAccount" : "389435993474",
        "kms:ViaService" : "ec2.region.amazonaws.com"
      }
    }
  }, {
    "Sid" : "Enable cross account log decryption",
    "Effect" : "Allow",
    "Principal" : {
      "AWS" : "*"
    },
    "Action" : [ "kms:Decrypt", "kms:ReEncryptFrom" ],
    "Resource" : "*",
    "Condition" : {
      "StringEquals" : {
        "kms:CallerAccount" : "389435993474"
      },
      "StringLike" : {
        "kms:EncryptionContext:aws:cloudtrail:arn" : "arn:aws:cloudtrail:*:389435993474:trail/*"
      }
    }
  } ]
}

